import React, { Component } from "react";
class Last extends Component {
    constructor(){
        super()
        this.state={
            lastname:"",
            txtname:""
        }
    }
    lastnameChange =(event)=>{
        this.setState({
            lastname:event.target.value,
        });
    }
    change =(event)=>{
        this.setState({
            txtname:this.state.lastname
        });
    }

    render(){
        return (

            <div>
                <div>
                <label>Lastname:</label>
                <input type="text"  value={this.state.firstname} onChange={this.lastnameChange}/>
                </div>
                <button onClick={this.change}>Click Me</button>
                <div>
               <h1>Last Name:{this.state.txtname}</h1> 
            </div>
            </div>



        )
    }
}
export default Last;
