import React, { useState } from 'react';

import { View, StyleSheet, Text, Button } from 'react-native';

export default function App() {

  const [someText, setSomeText] = useState('ReactNative-Examples.com');

  const convertToUpperCase = () => {
    let tempText = someText.toUpperCase();
    setSomeText(tempText);
  };

  const convertToLowerCase = () => {
    let tempText = someText.toLowerCase();
    setSomeText(tempText);
  };

  const convertToTitleCase = () => {
    let camelCaseText = someText
      .split(' ')
      .map(function (word, index) {
        return word.charAt(0)
          .toUpperCase() + word.slice(1)
            .toLowerCase();
      })
      .join(' ');
    setSomeText(camelCaseText);
  };


  return (
    <View style={styleSheet.MainContainer}>

      <Text style={{ color: 'red', fontSize: 30, textAlign: 'center' }}> {someText} </Text>

      <View style={{ marginTop: 12 }}>
        <Button
          title="Convert Text To Title Case"
          onPress={convertToTitleCase}
          color="#33691E"
        />
      </View>

      <View style={{ marginTop: 12 }}>
        <Button
          title="Convert Text To Upper Case"
          onPress={convertToUpperCase}
          color="#33691E"
        />
      </View>

      <View style={{ marginTop: 12 }}>
        <Button
          title="Convert Text To Lower Case"
          onPress={convertToLowerCase}
          color="#33691E"
        />
      </View>
    </View>
  );
}

const styleSheet = StyleSheet.create({

  MainContainer: {
    flex: 1,
    padding: 10,
    justifyContent: 'center',
    alignItems: 'center'
  }

});
