import React, { Component } from "react";
class Form extends Component {
    constructor(){
        super()
        this.state={
            firstname:"",
            txtname:""
        }
    }
    firstnameChange =(event)=>{
        this.setState({
            firstname:event.target.value,
        });
    }
    change =(event)=>{
        this.setState({
            txtname:event.target.firstname
        });
    }
    // lastnameChange =(event)=>{
    //     this.setState({
    //         lastname:event.target.value
    //     })
    // }
    render(){
        return (
            <form>
            <div>
                <label>Firstname:</label>
                <input type="text"  value={this.state.firstname} onChange={firstnameChange}/>
                <button onClick={this.change}>Click Me</button>
            </div>
            <div>
                <h1>Name:{this.txtname}</h1>
            </div>
             {/* <div>
             <label>Lastname:</label>
             <input type="text" value={this.state.lastname} onChange={this.state.lastnameChange}/>
         </div> */}
         </form>
        )
    }
}
export default Form;
