class Text extends Component {
    constructor(){
        super()
        this.state={
            textarea: "",
            txt:""
        }
    }
     textarea=toUpperCase();
    firstnameChange =(event)=>{
        this.setState({
            textarea:event.target.value,
        })
    }
   render(){
       return(

       <div>
        <div className="app">
        <label>Text Area:</label>
        <input type="text area" value={this.state.firstname} onChange={this.firstnameChange}/>
      </div> 
      <button onClick={this.change}>UpperCase</button>
      <button onClick={this.change}>LowerCase</button>
      </div>

       )
   }
                }

export default Text;
