import React, { Component } from "react";
class Text extends Component {
    constructor(){
        super()
        this.state={
            areatext: "",
            txt:""
        }
        }
//   var txt:toUpperCase();
    firstnameChange =(event)=>{
        this.setState({
            areatext:event.target.value,
            // var:this.textarea.length,       
        })
    }
    change =(event)=>{
        this.setState({
          txt:this.state.text,
          text:this.areatext.toUpperCase(),
        })
    }
    lower = (event)=>{
        this.setState({
            txt:this.state.text,
            text:this.areatext.toLowerCase()
        })
    }
   render(){
       return(

       <div>
        <div className="app">
        <label>Text Area:</label>
        <textarea rows="3" id="users" value={this.state.text} onChange={this.firstnameChange}/>
      </div> 
      <button onClick={this.change}>UpperCase</button>
      <button onClick={this.lower}>LowerCase</button>
      <div>
      <h1>Your First Name: {this.state.txt}</h1>
      </div>
      </div>
       )
   }
                }

export default Text;
